-- ----------------------------
-- Function structure for fn_ThanhTien
-- ----------------------------
DROP FUNCTION IF EXISTS `fn_ThanhTien`;
delimiter ;;
CREATE FUNCTION `fn_ThanhTien`(SoDonDH int)
 RETURNS float
begin
 return (select sum((soluong * giadathang) - giamgia)
 from chitietdathang ct where ct.soDonDH = SoDonDH);
end
;;
delimiter ;

-- ----------------------------
-- View structure for view_danhsachdonhang
-- ----------------------------
DROP VIEW IF EXISTS `view_danhsachdonhang`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `view_danhsachdonhang` AS select CT.SoDonDH, MSHH, SoLuong, GiaDatHang, GiamGia, KH.MSKH, NV.MSNV, DATE_FORMAT(NgayDH,'%d/%m/%Y') AS NgayDH, DATE_FORMAT(NgayGH,'%d/%m/%Y') AS NgayGH, TrangThai, NV.HoTenNV, KH.HoTenKH, fn_ThanhTien(CT.SoDonDH) as ThanhTien
from chitietdathang CT JOIN dathang DH 
											ON CT.SoDonDH = DH.SoDonDH
											JOIN khachhang KH
											ON DH.MSKH = KH.MSKH
											JOIN nhanvien NV
											ON DH.MSNV = NV.MSNV ;

-- ----------------------------
-- View structure for view_danhsachhanghoa
-- ----------------------------
DROP VIEW IF EXISTS `view_danhsachhanghoa`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `view_danhsachhanghoa` AS select MSHH, TenHH, QuyCach, Gia, SoLuongHang, hh.MaLoaiHang, lh.TenLoaiHang, GhiChu, Hinh1, Hinh2, Hinh3
from hanghoa hh join loaihanghoa lh
on hh.maloaihang = lh.maloaihang ;

-- ----------------------------
-- View structure for view_danhsachkhachhang
-- ----------------------------
DROP VIEW IF EXISTS `view_danhsachkhachhang`;
CREATE ALGORITHM = UNDEFINED SQL SECURITY DEFINER VIEW `view_danhsachkhachhang` AS select kh.MSKH, HoTenKH, TenCongTy, dc.diachi, dc.MaDC, SoDienThoai, Email
from khachhang kh join diachikh dc
on dc.mskh = kh.mskh ;

-- ----------------------------
-- Function structure for fn_DoanhThuThang
-- ----------------------------
DROP FUNCTION IF EXISTS `fn_DoanhThuThang`;
delimiter ;;
CREATE FUNCTION `fn_DoanhThuThang`(thang int)
 RETURNS float
begin
 return (select SUM((soluong * giadathang) - giamgia)
 from chitietdathang ct join dathang dh on dh.SoDonDH = ct.SoDonDH
 where MONTH(NgayDH) = thang);
end
;;
delimiter ;


-- ----------------------------
-- Triggers structure for table chitietdathang
-- ----------------------------
DROP TRIGGER IF EXISTS `Trg_KiemTraSoLuongInsert`;
delimiter ;;
CREATE TRIGGER `Trg_KiemTraSoLuongInsert` BEFORE INSERT ON `chitietdathang` FOR EACH ROW BEGIN

 IF 
		 (SELECT count(*) FROM HangHoa hh
     WHERE hh.mshh = NEW.mshh and hh.soluonghang < NEW.SoLuong) > 0
 THEN
    SIGNAL SQLSTATE '45000'
    SET MESSAGE_TEXT = 'Số lượng hàng không được lớn hơn số lượng còn trong kho';
 ELSE
		UPDATE HANGHOA SET SOLUONGHANG = SOLUONGHANG - NEW.SOLUONG WHERE MSHH = NEW.MSHH;
 END IF;

END
;;
delimiter ;

-- ----------------------------
-- Triggers structure for table chitietdathang
-- ----------------------------
DROP TRIGGER IF EXISTS `Trg_KiemTraSoLuongUpdate`;
delimiter ;;
CREATE TRIGGER `Trg_KiemTraSoLuongUpdate` BEFORE UPDATE ON `chitietdathang` FOR EACH ROW BEGIN

	IF NEW.SOLUONG > OLD.SOLUONG
	THEN
	 IF 
			 (SELECT count(*) FROM HangHoa hh
			 WHERE hh.mshh = NEW.mshh and hh.soluonghang < NEW.SoLuong) > 0
	 THEN
			SIGNAL SQLSTATE '45000'
			SET MESSAGE_TEXT = 'Số lượng hàng không được lớn hơn số lượng còn trong kho';
	 ELSE
			UPDATE HANGHOA SET SOLUONGHANG = SOLUONGHANG + OLD.SOLUONG - NEW.SOLUONG WHERE MSHH = NEW.MSHH;
	 END IF;

  ELSE
	  UPDATE HANGHOA SET SOLUONGHANG = SOLUONGHANG + (OLD.SOLUONG - NEW.SOLUONG) WHERE MSHH = NEW.MSHH;
	END IF;

END
;;
delimiter ;

-- ----------------------------
-- Triggers structure for table chitietdathang
-- ----------------------------
DROP TRIGGER IF EXISTS `Trg_KiemTraSoLuongDelete`;
delimiter ;;
CREATE TRIGGER `Trg_KiemTraSoLuongDelete` BEFORE DELETE ON `chitietdathang` FOR EACH ROW BEGIN

	UPDATE HANGHOA SET SOLUONGHANG = SOLUONGHANG + old.soluong where mshh = old.mshh;

END
;;
delimiter ;

-- ----------------------------
-- Triggers structure for table khachhang
-- ----------------------------
DROP TRIGGER IF EXISTS `Trg_ThemDiaChiKH`;
delimiter ;;
CREATE TRIGGER `Trg_ThemDiaChiKH` AFTER INSERT ON `khachhang` FOR EACH ROW BEGIN

	INSERT INTO DIACHIKH(MSKH, DIACHI) VALUES (NEW.MSKH, NEW.DIACHI);

END
;;
delimiter ;

SET FOREIGN_KEY_CHECKS = 1;
